// --- API Service ---
const API_BASE = 'http://localhost:8000/api/v1';

const api = {
  getProducts: async (search = '') => {
    const url = search ? `${API_BASE}/products?search=${encodeURIComponent(search)}` : `${API_BASE}/products`;
    const response = await fetch(url);
    if (!response.ok) throw new Error('Failed to fetch products');
    return response.json();
  },
  
  getProduct: async (id) => {
    const response = await fetch(`${API_BASE}/products/${id}`);
    if (!response.ok) throw new Error('Product not found');
    return response.json();
  },
  
  getBlogPosts: async () => {
    const response = await fetch(`${API_BASE}/blog`);
    if (!response.ok) throw new Error('Failed to fetch blog posts');
    return response.json();
  },
  
  getBlogPost: async (id) => {
    const response = await fetch(`${API_BASE}/blog/${id}`);
    if (!response.ok) throw new Error('Blog post not found');
    return response.json();
  },
  
  login: async (username, password) => {
    const formData = new FormData();
    formData.append('username', username);
    formData.append('password', password);
    
    const response = await fetch(`${API_BASE}/auth/login`, {
      method: 'POST',
      body: formData
    });
    
    if (!response.ok) throw new Error('Login failed');
    return response.json();
  },
  
  submitReview: async (reviewData) => {
    const response = await fetch(`${API_BASE}/reviews`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(reviewData)
    });
    return response.json();
  },

  createOrder: async (orderData) => {
    const response = await fetch(`${API_BASE}/orders`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(orderData)
    });
    if (!response.ok) throw new Error('Order creation failed');
    return response.json();
  },

  getOrders: async () => {
    const response = await fetch(`${API_BASE}/orders`);
    if (!response.ok) throw new Error('Failed to fetch orders');
    return response.json();
  }
};

// --- Helper Functions ---
const formatPrice = (price) => new Intl.NumberFormat('fa-IR').format(price) + ' تومان';
const formatDate = (dateStr) => new Date(dateStr).toLocaleDateString('fa-IR');

// --- کامپوننت Navigation ---
const Navbar = ({ 
  cartCount, 
  onCartClick, 
  onAuthClick, 
  user, 
  onLogout, 
  currentPage, 
  onPageChange,
  onSearch,
  searchTerm,
  setSearchTerm 
}) => {
  const [showSearchBox, setShowSearchBox] = useState(false);

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      onSearch(searchTerm);
      onPageChange('products');
      setShowSearchBox(false);
    }
  };

  return (
    <nav className="navbar">
      <div className="nav-container">
        <div className="nav-brand">
          <h1 onClick={() => onPageChange('home')}>iShop</h1>
        </div>
        
        <div className="nav-menu">
          <button 
            className={currentPage === 'home' ? 'active' : ''}
            onClick={() => onPageChange('home')}
          >
            خانه
          </button>
          <button 
            className={currentPage === 'products' ? 'active' : ''}
            onClick={() => onPageChange('products')}
          >
            محصولات
          </button>
          <button 
            className={currentPage === 'blog' ? 'active' : ''}
            onClick={() => onPageChange('blog')}
          >
            وبلاگ
          </button>
        </div>
        
        <div className="nav-actions">
          <div className="search-container">
            <button 
              className="search-btn"
              onClick={() => setShowSearchBox(!showSearchBox)}
            >
              <SearchIcon />
              <span>جستجو</span>
            </button>
            
            {showSearchBox && (
              <form 
                className="search-form"
                onSubmit={handleSearchSubmit}
              >
                <input
                  type="text"
                  placeholder="جستجو در محصولات..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="search-input-nav"
                  autoFocus
                />
                <button type="submit">جستجو</button>
              </form>
            )}
          </div>
          
          <button className="cart-btn" onClick={onCartClick}>
            <ShoppingCartIcon />
            <span>سبد خرید</span>
            {cartCount > 0 && <span className="cart-badge">{cartCount}</span>}
          </button>
          
          {user ? (
            <div className="user-dropdown">
              <button className="user-btn">
                <UserIcon />
                <span>{user.name}</span>
              </button>
              <div className="user-menu">
                <button onClick={() => onPageChange('dashboard')}>
                  <DashboardIcon />
                  داشبورد
                </button>
                <button onClick={onLogout}>
                  خروج
                </button>
              </div>
            </div>
          ) : (
            <button className="auth-btn" onClick={onAuthClick}>
              <UserIcon />
              <span>ورود</span>
            </button>
          )}
        </div>
      </div>
    </nav>
  );
};

