// --- صفحه محصولات ---
const ProductsPage = ({ products, onProductClick, onAddToCart, searchTerm }) => {
  const [localSearchTerm, setLocalSearchTerm] = useState(searchTerm || '');
  const [selectedCategory, setSelectedCategory] = useState('');
  
  const categories = useMemo(() => {
    const cats = [...new Set(products.map(p => p.category))];
    return cats.filter(Boolean);
  }, [products]);
  
  const filteredProducts = useMemo(() => {
    return products.filter(product => {
      const matchesSearch = product.name.toLowerCase().includes(localSearchTerm.toLowerCase()) ||
                           product.description.toLowerCase().includes(localSearchTerm.toLowerCase());
      const matchesCategory = !selectedCategory || product.category === selectedCategory;
      return matchesSearch && matchesCategory;
    });
  }, [products, localSearchTerm, selectedCategory]);
  
  return (
    <div className="products-page">
      <div className="container">
        <h1>تمام محصولات</h1>
        
        {/* فیلترها */}
        <div className="filters">
          <input
            type="text"
            placeholder="جستجو در محصولات..."
            value={localSearchTerm}
            onChange={(e) => setLocalSearchTerm(e.target.value)}
            className="search-input"
          />
          
          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            className="category-select"
          >
            <option value="">همه دسته‌ها</option>
            {categories.map(cat => (
              <option key={cat} value={cat}>{cat}</option>
            ))}
          </select>
        </div>
        
        {/* محصولات */}
        <div className="products-grid">
          {filteredProducts.map(product => (
            <div
              key={product.id}
              className="product-card"
              onClick={() => onProductClick(product)}
            >
              <div className="product-image">
                <img src={product.imageUrl} alt={product.name} />
              </div>
              <div className="product-info">
                <h3>{product.name}</h3>
                <p className="product-description">{product.description}</p>
                <div className="product-footer">
                  <span className="product-price">{formatPrice(product.price)}</span>
                  <button 
                    className="add-to-cart-btn"
                    onClick={(e) => {
                      e.stopPropagation();
                      onAddToCart(product);
                    }}
                  >
                    افزودن به سبد
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
        
        {filteredProducts.length === 0 && (
          <div className="no-results">
            <p>محصولی با این مشخصات یافت نشد.</p>
          </div>
        )}
      </div>
    </div>
  );
};

// --- جزئیات محصول ---
const ProductDetailPage = ({ product, onBack, onAddToCart }) => {
  const [reviewText, setReviewText] = useState('');
  const [reviewRating, setReviewRating] = useState(5);
  const [submitting, setSubmitting] = useState(false);
  
  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    
    try {
      await api.submitReview({
        product_id: product.id,
        rating: reviewRating,
        text: reviewText
      });
      
      setReviewText('');
      setReviewRating(5);
      alert('نظر شما با موفقیت ثبت شد!');
    } catch (error) {
      alert('خطا در ثبت نظر');
    }
    
    setSubmitting(false);
  };
  
  return (
    <div className="product-detail-page">
      <div className="container">
        <button className="back-btn" onClick={onBack}>
          <ArrowRightIcon />
          بازگشت
        </button>
        
        <div className="product-detail">
          <div className="product-image">
            <img src={product.imageUrl} alt={product.name} />
          </div>
          
          <div className="product-info">
            <h1>{product.name}</h1>
            <p className="product-description">{product.description}</p>
            <p className="product-price">{formatPrice(product.price)}</p>
            
            <div className="product-actions">
              <button 
                className="add-to-cart-btn large"
                onClick={() => onAddToCart(product)}
              >
                افزودن به سبد خرید
              </button>
              <button className="wishlist-btn">
                <HeartIcon />
                علاقه‌مندی‌ها
              </button>
            </div>
          </div>
        </div>
        
        {/* نظرات */}
        <div className="reviews-section">
          <h2>نظرات کاربران</h2>
          
          {product.reviews && product.reviews.length > 0 ? (
            <div className="reviews-list">
              {product.reviews.map((review, index) => (
                <div key={index} className="review">
                  <div className="review-header">
                    <span className="author">{review.author}</span>
                    <div className="rating">
                      {[...Array(5)].map((_, i) => (
                        <StarIcon key={i} filled={i < review.rating} />
                      ))}
                    </div>
                  </div>
                  <p className="review-text">{review.text}</p>
                </div>
              ))}
            </div>
          ) : (
            <p>هنوز نظری ثبت نشده است.</p>
          )}
          
          {/* فرم ثبت نظر */}
          <div className="review-form">
            <h3>نظر خود را ثبت کنید</h3>
            <form onSubmit={handleReviewSubmit}>
              <div className="rating-input">
                <span>امتیاز:</span>
                <div className="stars">
                  {[...Array(5)].map((_, i) => (
                    <button
                      key={i}
                      type="button"
                      onClick={() => setReviewRating(i + 1)}
                    >
                      <StarIcon filled={i < reviewRating} />
                    </button>
                  ))}
                </div>
              </div>
              
              <textarea
                value={reviewText}
                onChange={(e) => setReviewText(e.target.value)}
                placeholder="نظر خود را بنویسید..."
                required
              ></textarea>
              
              <button type="submit" disabled={submitting}>
                {submitting ? 'در حال ثبت...' : 'ثبت نظر'}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

