// --- داشبورد کاربری ---
const DashboardPage = ({ user }) => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api.getOrders()
      .then(setOrders)
      .catch(console.error)
      .finally(() => setLoading(false));
  }, []);

  const renderContent = () => {
    switch (activeTab) {
      case 'orders':
        return (
          <div className="orders-content">
            <h2>سفارشات من</h2>
            {loading ? (
              <p>در حال بارگذاری...</p>
            ) : orders.length > 0 ? (
              <div className="orders-list">
                {orders.map(order => (
                  <div key={order.id} className="order-card">
                    <div className="order-header">
                      <span>سفارش #{order.id}</span>
                      <span className={`order-status ${order.status.replace(' ', '-')}`}>
                        {order.status}
                      </span>
                    </div>
                    <div className="order-details">
                      <p>تاریخ: {formatDate(order.date)}</p>
                      <p>مبلغ: {formatPrice(order.total)}</p>
                      {order.tracking_code && (
                        <p>کد رهگیری: {order.tracking_code}</p>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p>هنوز سفارشی ثبت نکرده‌اید.</p>
            )}
          </div>
        );
      
      case 'wishlist':
        return (
          <div className="wishlist-content">
            <h2>علاقه‌مندی‌ها</h2>
            <p>این بخش در حال توسعه است.</p>
          </div>
        );
      
      case 'profile':
        return (
          <div className="profile-content">
            <h2>اطلاعات کاربری</h2>
            <div className="profile-form">
              <div className="form-group">
                <label>نام:</label>
                <input type="text" value={user?.name || ''} readOnly />
              </div>
              <div className="form-group">
                <label>ایمیل:</label>
                <input type="email" value={user?.email || ''} readOnly />
              </div>
              <p className="note">برای ویرایش اطلاعات با پشتیبانی تماس بگیرید.</p>
            </div>
          </div>
        );
      
      default:
        return (
          <div className="dashboard-overview">
            <h2>خوش آمدید {user?.name}</h2>
            <div className="dashboard-stats">
              <div className="stat-card">
                <h3>تعداد سفارشات</h3>
                <p className="stat-number">{orders.length}</p>
              </div>
              <div className="stat-card">
                <h3>علاقه‌مندی‌ها</h3>
                <p className="stat-number">0</p>
              </div>
            </div>
          </div>
        );
    }
  };

  return (
    <div className="dashboard-page">
      <div className="container">
        <h1>داشبورد کاربری</h1>
        
        <div className="dashboard-layout">
          <div className="dashboard-sidebar">
            <button 
              className={activeTab === 'dashboard' ? 'active' : ''}
              onClick={() => setActiveTab('dashboard')}
            >
              <DashboardIcon />
              داشبورد
            </button>
            <button 
              className={activeTab === 'orders' ? 'active' : ''}
              onClick={() => setActiveTab('orders')}
            >
              <OrdersIcon />
              سفارشات من
            </button>
            <button 
              className={activeTab === 'wishlist' ? 'active' : ''}
              onClick={() => setActiveTab('wishlist')}
            >
              <HeartIcon />
              علاقه‌مندی‌ها
            </button>
            <button 
              className={activeTab === 'profile' ? 'active' : ''}
              onClick={() => setActiveTab('profile')}
            >
              <UserIcon />
              پروفایل
            </button>
          </div>
          
          <div className="dashboard-content">
            {renderContent()}
          </div>
        </div>
      </div>
    </div>
  );
};

// --- مودال احراز هویت ---
const AuthModal = ({ isOpen, onClose, onLogin }) => {
  const [email, setEmail] = useState('admin@ishop.com');
  const [password, setPassword] = useState('admin123');
  const [loading, setLoading] = useState(false);
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await onLogin(email, password);
      onClose();
    } catch (error) {
      alert('خطا در ورود: ' + error.message);
    }
    setLoading(false);
  };
  
  if (!isOpen) return null;
  
  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="auth-modal" onClick={e => e.stopPropagation()}>
        <button className="modal-close" onClick={onClose}>
          <CloseIcon />
        </button>
        <h2>ورود به حساب کاربری</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="email"
            placeholder="ایمیل"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="رمز عبور"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <div className="auth-actions">
            <button type="submit" disabled={loading}>
              {loading ? 'در حال ورود...' : 'ورود'}
            </button>
            <button type="button" onClick={onClose}>لغو</button>
          </div>
        </form>
        <div className="auth-hint">
          <p>اطلاعات تست: admin@ishop.com / admin123</p>
        </div>
      </div>
    </div>
  );
};

// --- سایدبار سبد خرید ---
const CartSidebar = ({ isOpen, onClose, items, onUpdateQuantity, onRemove, total, onCheckout }) => {
  if (!isOpen) return null;
  
  return (
    <div className="sidebar-overlay" onClick={onClose}>
      <div className="cart-sidebar" onClick={e => e.stopPropagation()}>
        <div className="cart-header">
          <h2>سبد خرید</h2>
          <button onClick={onClose}>
            <CloseIcon />
          </button>
        </div>
        
        <div className="cart-items">
          {items.length === 0 ? (
            <div className="empty-cart">
              <p>سبد خرید شما خالی است</p>
              <button onClick={onClose}>ادامه خرید</button>
            </div>
          ) : (
            items.map(item => (
              <div key={item.id} className="cart-item">
                <img src={item.imageUrl} alt={item.name} />
                <div className="item-info">
                  <h4>{item.name}</h4>
                  <p>{formatPrice(item.price)}</p>
                  <div className="quantity-controls">
                    <button onClick={() => onUpdateQuantity(item.id, item.quantity - 1)}>-</button>
                    <span>{item.quantity}</span>
                    <button onClick={() => onUpdateQuantity(item.id, item.quantity + 1)}>+</button>
                  </div>
                </div>
                <button className="remove-btn" onClick={() => onRemove(item.id)}>
                  <CloseIcon />
                </button>
              </div>
            ))
          )}
        </div>
        
        {items.length > 0 && (
          <div className="cart-footer">
            <div className="cart-total">
              مجموع: {formatPrice(total)}
            </div>
            <button className="checkout-btn" onClick={onCheckout}>
              تکمیل خرید
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

// --- فوتر ---
const Footer = ({ onPageChange }) => {
  return (
    <footer className="footer">
      <div className="container">
        <div className="footer-content">
          <div className="footer-section">
            <h3>iShop</h3>
            <p>فروشگاه آنلاین محصولات اورجینال با بهترین قیمت و خدمات پس از فروش.</p>
          </div>
          
          <div className="footer-section">
            <h4>لینک‌های مفید</h4>
            <ul>
              <li><button onClick={() => onPageChange('home')}>خانه</button></li>
              <li><button onClick={() => onPageChange('products')}>محصولات</button></li>
              <li><button onClick={() => onPageChange('blog')}>وبلاگ</button></li>
            </ul>
          </div>
          
          <div className="footer-section">
            <h4>خدمات مشتریان</h4>
            <ul>
              <li><a href="#faq">سوالات متداول</a></li>
              <li><a href="#terms">قوانین و مقررات</a></li>
              <li><a href="#guide">راهنمای خرید</a></li>
            </ul>
          </div>
          
          <div className="footer-section">
            <h4>تماس با ما</h4>
            <ul>
              <li>تهران، خیابان ولیعصر</li>
              <li>021-12345678</li>
              <li>info@ishop.com</li>
            </ul>
          </div>
        </div>
        
        <div className="footer-bottom">
          <p>&copy; 2024 فروشگاه iShop. تمامی حقوق محفوظ است.</p>
        </div>
      </div>
    </footer>
  );
};

// --- کامپوننت اصلی اپلیکیشن ---
function App() {
  // State ها
  const [currentPage, setCurrentPage] = useState('home');
  const [products, setProducts] = useState([]);
  const [blogPosts, setBlogPosts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [selectedBlogPost, setSelectedBlogPost] = useState(null);
  const [cartItems, setCartItems] = useState([]);
  const [user, setUser] = useState(null);
  const [showAuthModal, setShowAuthModal] = useState(false);
  const [showCart, setShowCart] = useState(false);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  
  // بارگذاری اولیه
  useEffect(() => {
    const loadData = async () => {
      try {
        const [productsData, blogData] = await Promise.all([
          api.getProducts(),
          api.getBlogPosts()
        ]);
        
        setProducts(productsData);
        setBlogPosts(blogData);
      } catch (error) {
        console.error('خطا در بارگذاری داده‌ها:', error);
        alert('خطا در اتصال به سرور. لطفاً بررسی کنید که backend در حال اجرا باشد.');
      }
      setLoading(false);
    };
    
    loadData();
  }, []);
  
  // مدیریت سبد خرید
  const addToCart = useCallback((product) => {
    setCartItems(prev => {
      const existing = prev.find(item => item.id === product.id);
      if (existing) {
        return prev.map(item =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...prev, { ...product, quantity: 1 }];
    });
    
    // نمایش پیام موفقیت
    alert(`${product.name} به سبد خرید اضافه شد!`);
  }, []);
  
  const updateCartQuantity = useCallback((productId, newQuantity) => {
    if (newQuantity <= 0) {
      setCartItems(prev => prev.filter(item => item.id !== productId));
    } else {
      setCartItems(prev =>
        prev.map(item =>
          item.id === productId ? { ...item, quantity: newQuantity } : item
        )
      );
    }
  }, []);
  
  const removeFromCart = useCallback((productId) => {
    setCartItems(prev => prev.filter(item => item.id !== productId));
  }, []);
  
  // محاسبات سبد خرید
  const cartTotal = useMemo(() => {
    return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  }, [cartItems]);
  
  const cartCount = useMemo(() => {
    return cartItems.reduce((count, item) => count + item.quantity, 0);
  }, [cartItems]);
  
  // مدیریت صفحات
  const handlePageChange = (page) => {
    setCurrentPage(page);
    setSelectedProduct(null);
    setSelectedBlogPost(null);
  };
  
  const handleProductClick = async (product) => {
    try {
      const fullProduct = await api.getProduct(product.id);
      setSelectedProduct(fullProduct);
      setCurrentPage('productDetail');
    } catch (error) {
      console.error('خطا در بارگذاری جزئیات محصول:', error);
    }
  };
  
  const handleBlogPostClick = async (post) => {
    try {
      const fullPost = await api.getBlogPost(post.id);
      setSelectedBlogPost(fullPost);
      setCurrentPage('blogPost');
    } catch (error) {
      console.error('خطا در بارگذاری مطلب:', error);
    }
  };
  
  // جستجو
  const handleSearch = async (term) => {
    try {
      setLoading(true);
      const searchResults = await api.getProducts(term);
      setProducts(searchResults);
      setSearchTerm(term);
    } catch (error) {
      console.error('خطا در جستجو:', error);
    } finally {
      setLoading(false);
    }
  };
  
  // احراز هویت
  const handleLogin = async (email, password) => {
    try {
      const response = await api.login(email, password);
      if (response.access_token) {
        setUser({ name: 'کاربر', email });
        localStorage.setItem('token', response.access_token);
      }
    } catch (error) {
      throw new Error('نام کاربری یا رمز عبور اشتباه است');
    }
  };
  
  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('token');
    if (currentPage === 'dashboard') {
      setCurrentPage('home');
    }
  };
  
  // تکمیل خرید
  const handleCheckout = async () => {
    try {
      const orderData = {
        total: cartTotal,
        items: cartItems.map(item => ({
          product_id: item.id,
          quantity: item.quantity,
          price: item.price
        }))
      };
      
      const order = await api.createOrder(orderData);
      
      // پاک کردن سبد خرید
      setCartItems([]);
      setShowCart(false);
      
      alert(`سفارش با موفقیت ثبت شد!\nکد رهگیری: ${order.tracking_code}`);
      
    } catch (error) {
      console.error('خطا در ثبت سفارش:', error);
      alert('خطا در ثبت سفارش. لطفاً دوباره تلاش کنید.');
    }
  };
  
  // نمایش صفحه بارگذاری
  if (loading) {
    return (
      <div className="loading-screen">
        <div className="loading-spinner"></div>
        <p>در حال بارگذاری...</p>
      </div>
    );
  }
  
  // رندر محتوا
  const renderContent = () => {
    switch (currentPage) {
      case 'products':
        return (
          <ProductsPage
            products={products}
            onProductClick={handleProductClick}
            onAddToCart={addToCart}
            searchTerm={searchTerm}
          />
        );
      
      case 'blog':
        return (
          <BlogPage
            posts={blogPosts}
            onPostClick={handleBlogPostClick}
          />
        );
      
      case 'productDetail':
        return selectedProduct ? (
          <ProductDetailPage
            product={selectedProduct}
            onBack={() => handlePageChange('products')}
            onAddToCart={addToCart}
          />
        ) : null;
      
      case 'blogPost':
        return selectedBlogPost ? (
          <BlogPostPage
            post={selectedBlogPost}
            onBack={() => handlePageChange('blog')}
          />
        ) : null;
      
      case 'dashboard':
        return user ? (
          <DashboardPage user={user} />
        ) : (
          <>
            {setShowAuthModal(true)}
            {handlePageChange('home')}
          </>
        );
      
      default:
        return (
          <HomePage
            products={products}
            onProductClick={handleProductClick}
            onAddToCart={addToCart}
          />
        );
    }
  };
  
  return (
    <div className="App">
      <Navbar
        cartCount={cartCount}
        onCartClick={() => setShowCart(true)}
        onAuthClick={() => setShowAuthModal(true)}
        user={user}
        onLogout={handleLogout}
        currentPage={currentPage}
        onPageChange={handlePageChange}
        onSearch={handleSearch}
        searchTerm={searchTerm}
        setSearchTerm={setSearchTerm}
      />
      
      {renderContent()}
      
      <Footer onPageChange={handlePageChange} />
      
      {/* مودال‌ها */}
      <AuthModal
        isOpen={showAuthModal}
        onClose={() => setShowAuthModal(false)}
        onLogin={handleLogin}
      />
      
      <CartSidebar
        isOpen={showCart}
        onClose={() => setShowCart(false)}
        items={cartItems}
        onUpdateQuantity={updateCartQuantity}
        onRemove={removeFromCart}
        total={cartTotal}
        onCheckout={handleCheckout}
      />
    </div>
  );
}

export default App;
